<template>
	<div v-if="poll.consensVote" class="vote-table-item" :class="[answer, { active: isActive}]">
		<select class="input" @click="voteClick()" v-model="answer">
			<option v-for="number of Array(11).keys()" :value="number">{{ number }}</option>
		</select>
	</div>
	<div v-else class="vote-table-item" :class="[answer, { active: isActive}]">
		<div class="icon" @click="voteClick()" />
	</div>
</template>

<script>
import { mapState } from 'vuex'

export default {
	name: 'VoteTableItem',

	props: {
		option: {
			type: Object,
			default: undefined
		},
		userId: {
			type: String,
			default: ''
		}
	},

	computed: {
		...mapState({
			poll: state => state.poll,
			acl: state => state.acl
		}),

		answer() {
			try {
				return this.$store.getters.getVote({
					option: this.option,
					userId: this.userId
				}).voteAnswer
			} catch (e) {
				return ''
			}
		},

		isValidUser() {
			return (this.userId !== '' && this.userId !== null)
		},

		isActive() {
			return (this.isValidUser && this.acl.userId === this.userId && this.acl.allowVote)
		}

	},

	methods: {
		voteClick() {
			if (this.isActive) {
				this.$emit('voteClick')
			}
		}

	}
}

</script>

